const sqlite3 = require('sqlite3').verbose();
var db;
exports.db = db;

exports.open = function (DBSOURCE, enableFK) {
    if(enableFK){
        return new Promise(function (resolve) {
            this.db = new sqlite3.Database(DBSOURCE,
                (err) => {
                    if (err) reject("AA-SQLite3Open error: " + err.message);
                    else {
                        var fk = this.db.run("PRAGMA foreign_keys=ON");
                        // console.log("AA-SQLite3:", fk);
                        resolve(DBSOURCE + " opened", "FK:", fk);
                    }
                }
            );
        });
    }
    else {
        return new Promise(function (resolve) {
            this.db = new sqlite3.Database(DBSOURCE,
                (err) => {
                    if (err) reject("AA-SQLite3Open error: " + err.message);
                    else {
                        var fk = this.db.run("PRAGMA foreign_keys=OFF");
                        // console.log("AA-SQLite3:", fk);
                        resolve(DBSOURCE + " opened", "FK:", fk);
                    }
                }
            );
        });
    }
    
};

// any query: insert/delete/update
exports.run = function (query) {
    return new Promise(function (resolve, reject) {
        this.db.run(query,
            function (err) {
                if (err) reject(err.message);
                else resolve(true);
            });
    });
};

// first row read
exports.get = function (query, params) {
    return new Promise(function (resolve, reject) {
        this.db.get(query, params, function (err, row) {
            if (err) reject("Read error: " + err.message);
            else {
                resolve(row);
            }
        });
    });
};

exports.get_all = function (query, params) {
    return new Promise(function (resolve, reject) {
        this.db.all(query, params, (err, rows) => {
            if (err) {
                reject(err.message);
            }
            else {
                returnValue = {
                    "message": "success",
                    "data": rows
                };
                resolve(returnValue);
            }
        });
    });//Return promise
};//get_all


exports.push = function (query, params) {
    return new Promise(function (resolve, reject) {
        if (params == undefined) params = [];
        this.db.all(query, params, function (err, rows) {
            if (err) reject({ message: "Failed", err: err });
            else {
                resolve(
                    { 
                        message: "Succeeded"
                    });
            }
        });
    });
};

// set of rows read
exports.all = function (query, params) {
    return new Promise(function (resolve, reject) {
        if (params == undefined) params = [];

        this.db.all(query, params, function (err, rows) {
            if (err) reject("Read error: " + err.message);
            else {
                resolve(rows);
            }
        });
    });
};

// each row returned one by one 
exports.each = function (query, params, action) {
    return new Promise(function (resolve, reject) {
        var db = this.db;
        db.serialize(function () {
            db.each(query, params, function (err, row) {
                if (err) reject("Read error: " + err.message);
                else {
                    if (row) {
                        action(row);
                    }
                }
            });
            db.get("", function (err, row) {
                resolve(true);
            });
        });
    });
}

exports.close = function () {
    return new Promise(function (resolve, reject) {
        this.db.close();
        resolve(true);
    });
};